import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { removeTask, updateTask } from '../redux/actions';

const TaskItem = ({ task }) => {
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);
  const [newText, setNewText] = useState(task.text);

  const handleRemoveTask = () => {
    dispatch(removeTask(task.id));
  };

  const handleUpdateTask = () => {
    if (newText.trim()) {
      dispatch(updateTask(task.id, newText));
      setIsEditing(false);
    }
  };

  return (
    <div className="flex items-center justify-between p-2 border-b">
      {isEditing ? (
        <>
          <input
            type="text"
            value={newText}
            onChange={(e) => setNewText(e.target.value)}
            className="border rounded p-2 w-full"
          />
          <button onClick={handleUpdateTask} className="bg-green-500 text-white p-1 rounded">
            Save
          </button>
        </>
      ) : (
        <>
          <span>{task.text}</span>
          <button onClick={() => setIsEditing(true)} className="bg-blue-500 text-white p-1 rounded">
            Edit
          </button>
          <button onClick={handleRemoveTask} className="bg-red-500 text-white p-1 rounded">
            Remove
          </button>
        </>
      )}
    </div>
  );
};

export default TaskItem;
