export const addTask = (text, category, dueDate) => ({
  type: 'ADD_TASK',
  payload: { text, category, dueDate },
});

export const removeTask = (id) => ({
  type: 'REMOVE_TASK',
  payload: id,
});

export const updateTask = (id, newText, newCategory, newDueDate) => ({
  type: 'UPDATE_TASK',
  payload: { id, newText, newCategory, newDueDate },
});

export const toggleCompleteTask = (id) => ({
  type: 'TOGGLE_COMPLETE_TASK',
  payload: id,
});
