import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { removeTask, updateTask, toggleCompleteTask } from '../redux/taskReducer';

const TaskItem = ({ task }) => {
  const dispatch = useDispatch();
  const [isEditing, setIsEditing] = useState(false);
  const [newText, setNewText] = useState(task.text);
  const [newCategory, setNewCategory] = useState(task.category);
  const [newDueDate, setNewDueDate] = useState(task.dueDate);

  const handleRemoveTask = () => {
    dispatch(removeTask(task.id));
  };

  const handleUpdateTask = () => {
    if (newText.trim()) {
      dispatch(updateTask({ id: task.id, newText, newCategory, newDueDate }));
      setIsEditing(false);
    }
  };

  const handleToggleCompleteTask = () => {
    dispatch(toggleCompleteTask(task.id));
  };

  return (
    <div className={`flex items-center justify-between p-2 border-b ${task.completed ? 'line-through' : ''}`}>
      {isEditing ? (
        <>
          <input
            type="text"
            value={newText}
            onChange={(e) => setNewText(e.target.value)}
            className="border rounded p-2 w-full"
          />
          <input
            type="text"
            value={newCategory}
            onChange={(e) => setNewCategory(e.target.value)}
            className="border rounded p-2 w-full mt-2"
          />
          <input
            type="date"
            value={newDueDate}
            onChange={(e) => setNewDueDate(e.target.value)}
            className="border rounded p-2 w-full mt-2"
          />
          <button onClick={handleUpdateTask} className="bg-green-500 text-white p-1 rounded">
            Save
          </button>
        </>
      ) : (
        <>
          <div>
            <span>{task.text}</span>
            <span className="ml-2 text-gray-500">{task.category}</span>
            <span className="ml-2 text-gray-500">{task.dueDate}</span>
          </div>
          <div>
            <button onClick={handleToggleCompleteTask} className="bg-green-500 text-white p-1 rounded mr-2">
              {task.completed ? 'Undo' : 'Complete'}
            </button>
            <button onClick={() => setIsEditing(true)} className="bg-blue-500 text-white p-1 rounded mr-2">
              Edit
            </button>
            <button onClick={handleRemoveTask} className="bg-red-500 text-white p-1 rounded">
              Remove
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default TaskItem;
